import React, { useState } from "react";

function Dashboard() {
  const [plate, setPlate] = useState("");
  const [vehicle, setVehicle] = useState(null);

  const handleSearch = () => {
    // Dummy Data (replace with backend later)
    const dummyVehicle = {
      name: "Rahul Kumar",
      department: "AI & DS",
      contact: "Hidden (Use Official Security Line)"
    };

    setVehicle(dummyVehicle);
  };

  return (
    <div className="dashboard-container">
      <h2>Security Dashboard</h2>

      <input
        placeholder="Enter Vehicle Number"
        value={plate}
        onChange={(e) => setPlate(e.target.value)}
      />

      <button onClick={handleSearch}>Search Vehicle</button>

      {vehicle && (
        <div className="vehicle-card">
          <h3>Owner Details</h3>
          <p><b>Name:</b> {vehicle.name}</p>
          <p><b>Department:</b> {vehicle.department}</p>
          <p><b>Contact:</b> {vehicle.contact}</p>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
